css:
/*pc*/@media (min-width: 601px){
  .top-bar__nav {
    color: #ffffff;}
  .header{box-shadow: -6px 15px 14px -9px rgb(167 167 167 / 37%);border-bottom: 1px solid #cbb9a0;
}
  .header__nav a:hover {background: transparent;
    font-size: 14px;font-weight:400;
    text-decoration: none;}
   .header__nav a:hover {background: transparent;font-size: 14px;font-weight:700;text-decoration: none;
    }
  .top-bar__page-links a{text-decoration: none;
